<script type="text/javascript">
  $(document).ready(function () {
    document.cookie = "collapse=;path=/;";
    //VALIDATION FORM
    $('#login_form .isian').bind('input', function (e) {
      validate_form();
    });

    //CAPTCHA
    refresh_captcha(); 
    $("#submit_captcha").click(function() {
      if (eval($("#ask_captcha").text()) == $("#answer_captcha").val()) {
        swal("Ok! You are human!");
        $("#status_captcha").val('OK');
      } else {
        $("#answer_captcha").val('');
        $("#status_captcha").val('');
        swal("Error");
      }
      validate_form();
    });

    $('#login_form #answer_captcha').bind('input', function (e) {
      if($(this).val()==''){
        $("#status_captcha").val('');
      }
      validate_form();
    });
  });

  function validate_form(){
    //VALIDATION FORM
    var $nonempty = $('.isian').filter(function() {
      return this.value != ''
    });

    var count = $(".isian").length;

    if ($nonempty.length == count) {
      $("#submit_form").removeAttr('disabled');
      $('#submit_form').tooltip('dispose');
    }else{
      $("#submit_form").attr('disabled','disabled');
    }
    console.log($nonempty.length);
  }

  function show_password() {
    if ($("#password").attr("type") === "password") {
      $("#password").attr("type","text");
      $("#icon_password").attr('class', 'fas fa-eye');
      $('#icon_password').tooltip('disable');
    } else {
      $("#password").attr("type","password");
      $("#icon_password").attr('class', 'fas fa-eye-slash');
      $('#icon_password').tooltip('enable');
    }
  } 

  function refresh_captcha(){
    var n1 = Math.round(Math.random() * 10 + 1);
    var n2 = Math.round(Math.random() * 10 + 1);
    $("#ask_captcha").text(n1 + " + " + n2);
  }
  
  function validate_number(root){
    var reet = root.value;
    var arr1= reet.length;
    var ruut = reet.charAt(arr1-1);
    if (reet.length > 0){
        var regex = /[0-9]|\./;
        if (!ruut.match(regex)){
            var reet = reet.slice(0, -1);
            $(root).val(reet);
        }
    }
  }
</script></body></html>